export default(state,action)=>{
    switch(action.type){
        case 'ADD_TASK':
            return{
                task:[action.payload,...state.task]
            }

        case 'CHECK_TASK':
            const checkedTask = action.payload;
            const checkingTask = state.task.map(task =>{
                if(task.id === checkedTask.id){
                    return checkedTask
                }
                return task
            })
            return{
                task:checkedTask
            }
        default:
            return state
    }
}
