import React,{createContext, useReducer} from 'react';


//Initial state
const initialState = {
    task:[
        {
            id:1,
            content:'Design register page',
            completion:false,
        },
        {
            id:2,
            content:'Design login page',
            completion:false,
        },
    ]
};

//Create Context
export const GlobalContext = createContext(initialState);

//Provide component
export const GlobalProvider = ({children})=>{
    const[state,dispatch]=useReducer(initialState);

    //Action
    const removeTask=(id)=>{
        dispatch({
            type:'REMOVE_TASK',
            payload: id
        })
    }

    const addTask=(task)=>{
        dispatch({
            type:'ADD_TASK',
            payload:task
        })
    }
    const editTask=(task)=>{
        dispatch({
            type:'EDIT_TASK',
            payload:task
        })
    }

    return(
        <GlobalContext.Provider value={{
            task:state.task,
            removeTask,
            editTask,
            addTask
        }}>
            {children}
        </GlobalContext.Provider>
    )
}
