import React,{useState, useEffect} from 'react';
import {
    Form,
    FormGroup,
    Input,
    Label,
    Container,
    Button
}from 'reactstrap';
import {useForm} from '../Hooks/useForm';
import {useFetch} from '../Hooks/useFetch';

export const Forms =()=>{
    const {data,loading}= useFetch('http://numbersapi.com/43/trivia');
    // const [{name,contact},setState]=useState({name:'',contact:''});
    // const onChange=(e)=>{
    //     setState([e.target.name]=e.target.value)
    // }
    const [state,handleChange]=useForm({name:'',contact:''});
    const [count,setCount] = useState(0)

    // useEffect(()=>{
    //     const onClick=(e)=>{
    //         console.log(e)
    //     }
    //     const onClicked=(e)=>{
    //         console.log('removed')
    //     }
    //     console.log('render')
    //     window.addEventListener('click', onClick);
    //     return(
    //         window.addEventListener('click',onClicked)
    //     )
    // })

    return(
        <Container>
            <p>count:{count}</p>
            <Button onClick={()=>setCount(c=>c+1)}>+</Button>
            <div>{loading ? '...wait':data}</div>
            <Form>
                <FormGroup>
                    <Label>Name</Label>
                    <Input type="text" value={state.name} onChange={handleChange} name="name" />
                </FormGroup>
                <FormGroup>
                    <Label>Contact</Label>
                    <Input type="text" value={state.contact} onChange={handleChange} name="contact"/>
                </FormGroup>
            </Form>
        </Container>        
    )
}
