export default(state,action)=>{
    switch(action.type){
        case 'ADD_TASK':
            return{
                task:[action.payload,...state.task]
            }

        case 'DO_TASK':
            return{
                task:state.task.map(task=>{
                    if(task.id === action.id){
                        return{
                            task:{...task, completion: true}
                        }
                    }
                    return task
                })
            }
        default:
            return state
    }
}
