export default(state,action)=>{
    switch(action.type){
        case 'ADD_TASK':
            return{
                task:[action.payload,...state.task]
            }

        case 'DO_TASK':
            const tasks=state.task.map(task =>{
                if(task.id === action.payload.id){
                    return{ ...task, completion: !task.completion}
                }
                return task;
            });
            return{
                ...state,
                tasks
            }
        default:
            return state
    }
}
