import React,{useState,useEffect} from 'react';


export const Api=()=>{
    const [info,setInfo] = useState('');
    const url = 'https://api.adviceslip.com/advice';
    const handleAlert =()=>{
            fetch(url)
            .then((res)=> res.json())
            .then((data)=>{
                console.log(data)
            })
    }
    useEffect(()=>{
        handleAlert()
    },[])
    return(
        <div>
            <p>you have pressed {info} times</p>
            <button onClick={()=> setInfo(handleAlert)}>Click</button>
        </div>
    )
}
