import React,{useState, useEffect} from 'react';
import {useForm} from './useForm';
import {Info} from './Info';
import {useFetch} from './useFetch';

export const Items =()=>{
    const[value,handleChange]=useForm({name:'', age:''});
    const {data, loading}= useFetch('http://numbersapi.com/43/trivia');
    const [count,setCount] = useState(0);
    // // const [showInfo, setShowInfo]= useState(true);

    // const onMouseMove=(e)=>{
    //     console.log(e);
    // }
    // useEffect(()=>{
    //     console.log('render');
    //     window.addEventListener('mousemove',onMouseMove)

    //     return()=>{
    //         console.log('unmount')
    //         window.addEventListener('mousemove',onMouseMove)
    //     }
    // },[value.age])



    return(
        <div>
            <p>{count}</p>
            <button onClick={()=>setCount(count => count + 1)}>Increment</button>
            <p>{loading ? 'wait...':data}</p>
            {/* <button onClick={()=>setShowInfo(!showInfo)}>Toggle</button>
            {showInfo && <Info/>} */}
            <form>
                <input type="text" value={value.name} onChange={handleChange} name="name"/>
                <input type="text" value={value.age} onChange={handleChange} name="age"/>
            </form>
        </div>
    )
}
