import React,{useState, useEffect} from 'react';
import {
    Form,
    FormGroup,
    Input,
    Label,
    Container,
    Button
}from 'reactstrap';
import {useForm} from '../Hooks/useForm';

export const Forms =()=>{
    // const [{name,contact},setState]=useState({name:'',contact:''});
    // const onChange=(e)=>{
    //     setState([e.target.name]=e.target.value)
    // }
    const [state,handleChange]=useForm({name:'',contact:''});

    useEffect(()=>{
        console.log('render')

        // return(
        //     console.log('unmount')
        // )
    },[state.name])
    return(
        <Container>
            <Form>
                <FormGroup>
                    <Label>Name</Label>
                    <Input type="text" value={state.name} onChange={handleChange} name="name" />
                </FormGroup>
                <FormGroup>
                    <Label>Contact</Label>
                    <Input type="text" value={state.contact} onChange={handleChange} name="contact"/>
                </FormGroup>
            </Form>
        </Container>        
    )
}
