import React,{useContext} from 'react';
import {Ellipsis} from '../../assets/index';
import {GlobalContext} from '../../Context/GlobalState';
import {useForm} from '../../Hooks/useForm';

export const TodayTask=()=>{
    const {task} = useContext(GlobalContext);
    return(
        <div>
            <div className='today-task-header'>
                <h3>Today's Tasks</h3>
                <img src={Ellipsis} alt="" width='6px' height='24px'/>
            </div>
            <div className='today-task-complete'>
            {task.length > 0 ? (task.map(task =>{
                if (task.completion === true){
                    return(
                        <>
                        <input type="checkbox" checked/>
                        <label style={{marginLeft:'1rem', color:'black'}}>{task.content}</label><br/>
                        </>     
                    )            
                }
                else{
                    return(
                        <p>No task left</p>
                    )
                }
            })):<p>No task today</p>}
            </div>
            <div className='today-task-list'>
            {task.length > 0 ? (task.map(task =>{
                if (task.completion === false){
                    return(
                        <>
                        <input type="checkbox"/>
                        <label>{task.content}</label><br/>
                        </>
                    )            
                }
                else{
                    return(
                        null
                    )
                }
            })):(<p>No task today</p>)}
            </div>
            <div className="today-task-add">
                <form>
                    <input type="text" onChange={useForm} placeholder="+ Add a new task"/>
                </form>
            </div>
        </div>
    )
}
