import React,{useContext,useState} from 'react';
import {Ellipsis} from '../../assets/index';
import {GlobalContext} from '../../Context/GlobalState';
// import {useForm} from '../../Hooks/useForm';
import {v4 as uuid} from 'uuid';

export const TodayTask=()=>{
    const[content,setContent] = useState('');
    const [check, setCheck] = useState(false);
    const {task, addTask} = useContext(GlobalContext);
    const onSubmit=(e)=>{
        e.preventDefault();
        const newTask={
            id:uuid(),
            content,
            completion:false
        }
        addTask(newTask);
    }
    const onChange=(e)=>{
        setContent(e.target.value);
    }
    const onClick=(e)=>{
        setCheck(e.target.checked);
    }

    return(
        <div>
            <div className='today-task-header'>
                <h3>Today's Tasks</h3>
                <img src={Ellipsis} alt="" width='6px' height='24px'/>
            </div>
            <div className='today-task-complete'>
            {task.length > 0 ? (task.map(task =>{
                if (task.completion === true){
                    return(
                        <>
                        <input type="checkbox" checked/>
                        <label style={{marginLeft:'1rem', color:'black'}}>{task.content}</label><br/>
                        </>     
                    )            
                }
            })):<p>No task today</p>}
            </div>
            <div className='today-task-list'>
            {task.length > 0 ? (task.map(task =>{
                if (task.completion === false){
                    return(
                        <>
                        <input type="checkbox" value={check} onClick={onClick}/>
                        <label>{task.content}</label><br/>
                        </>
                    )            
                }
                else{
                    return(
                        null
                    )
                }
            })):(<p>No task today</p>)}
            </div>
            <div className="today-task-add">
                <form onSubmit={onSubmit}>
                    <input type="text" value={content} onChange={onChange} placeholder="+ Add a new task"/>
                    <button type="submit">Submit</button>
                </form>
            </div>
        </div>
    )
}
